import { Button, Card, CardActions, CardContent, CardMedia, Typography } from "@material-ui/core";
import React from "react";
import useStyles from "./styles";
import ThumbUpdAltIcon from "@material-ui/icons/ThumbUpAlt";
import DeleteIcon from "@material-ui/icons/Delete";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import moment from 'moment'
import { useDispatch } from "react-redux";
import { deletePost,likePost } from "../../../actions/posts";
import ThumbUpAltOutlined from '@material-ui/icons/ThumbDownAltOutlined'

const Post = ({post ,setCurrentId}) => {
  const classes = useStyles();
	const dispatch = useDispatch();
	const user = JSON.parse(localStorage.getItem('profile'))
	const Likes = () => {
    if (post.likes.length > 0) {
       return post.likes.find((like) => like === user?.result?._id)
          ? (
             <><ThumbUpdAltIcon fontSize="small" />&nbsp;{post.likes.length > 2 ? `You and ${post.likes.length - 1} others` : `${post.likes.length} like${post.likes.length > 1 ? 's' : ''}`}</>
          ) : (
             <><ThumbUpAltOutlined fontSize="small" />&nbsp;{post.likes.length} {post.likes.length === 1 ? 'Like' : 'Likes'}</>
          );
    }

    return <><ThumbUpAltOutlined fontSize="small" />&nbsp;Like</>;
 };
  return (
    <Card className={classes.card}>
      <CardMedia
        className={classes.media}
        image={post.selectedFile}
        title={post.title}
      ></CardMedia>
				<div className={classes.overlay}>
					<Typography variant="h6">{post.name}</Typography>
					<Typography variant="body2">{moment(post.createdAt).fromNow()}</Typography>
				</div>
				<div className={classes.overlay2}>
					<Button style={{color:'white'}}
					 size="small" 
					 onClick={()=>setCurrentId(post._id)}>
					<MoreHorizIcon fontSize="medium" />
					</Button>

				</div>

				<div className={classes.details}>
					<Typography variant="body2" color="textSecondary">
						{post.tags.map((tag) =>`#${tag} `)}
						</Typography>
				</div>
				<Typography className={classes.title} variant="h5"  gutterBottom>
						{post.title}
						</Typography>
				<CardContent>
					<Typography variant="body2" require color="textSecondary" component="p">
						{post.message}
					</Typography>
				</CardContent>
				<CardActions className={classes.cardActions}>
					<Button size="small" color="primary" disabled={!user?.res} onClick={()=>dispatch(likePost(post._id))}>
					
						
					</Button>
					<Button size="small" color="primary" onClick={()=>dispatch(deletePost(post._id))}>
						<DeleteIcon fontSize="small"/>
							Delete
						
					</Button>
				</CardActions>
				
			
    </Card>
  );
};

export default Post;
